<script setup>
import { useStaticStore } from "~/store/static";

const staticStore = useStaticStore();
</script>

<template>
  <footer class="footer">
    <div class="container">
      <div class="footer__content">
        <NuxtLink no-prefetch :to="localePath('/')" class="footer__logo">
          <NuxtImg
            src="/images/logo.webp"
            :alt="$t('common.altLogo')"
            densities="x1 x2"
            width="80"
            height="40"
          />
        </NuxtLink>
        <div v-if="staticStore.menu.about">
          <p class="footer__grid__title">
            {{ staticStore.menu.about.name }}
          </p>
          <template v-for="link in staticStore.menu.about.items" :key="link.id">
            <NuxtLink no-prefetch :to="localePath(link.path)">{{
              link.name
            }}</NuxtLink>
          </template>
        </div>
        <div v-if="staticStore.menu.info">
          <p class="footer__grid__title">
            {{ staticStore.menu.info.name }}
          </p>
          <template v-for="link in staticStore.menu.info.items" :key="link.id">
            <NuxtLink no-prefetch :to="localePath(link.path)">{{
              link.name
            }}</NuxtLink>
          </template>
        </div>
        <div v-if="staticStore.menu.popular_categories">
          <p class="footer__grid__title">
            {{ staticStore.menu.popular_categories.name }}
          </p>
          <template
            v-for="link in staticStore.menu.popular_categories.items"
            :key="link.id"
          >
            <NuxtLink no-prefetch :to="localePath(link.path)">{{
              link.name
            }}</NuxtLink>
          </template>
        </div>
        <div v-if="staticStore.phones">
          <p class="footer__grid__title">{{ $t("footer.phonesBlockTitle") }}</p>
